import { ArrowBackIosOutlined, ArrowForwardIosOutlined } from '@material-ui/icons'
import React, { useRef, useState } from 'react'
import './list.scss'
import ListItem from '../../listItem/ListItem'

const List = () => {

    const [ slideNumber, setSlideNumber ] =  useState(0);
    const listRef = useRef();

    const handleClick = (direction) =>{
        let distance = listRef.current.getBoundingClientRect().x - 50; //50 is 50px from the Arrow
        //distance declaration can only be in handleClick function, otherwise current return undefined

        if( direction === 'left' && slideNumber >0){
            setSlideNumber(slideNumber-1)
            listRef.current.style.transform = `translateX(${distance + 230}px)`
        }else if(direction === 'right' && slideNumber <9) {

            setSlideNumber(slideNumber+1)
            listRef.current.style.transform = `translateX(${distance - 230}px)`
            
        }
    }

    return (
        <div className='list'>
            <span className="listTitle">Continue to Watch</span>
            <div className="wrapper">
                <ArrowBackIosOutlined className="sliderArrow left" onClick={() =>handleClick('left')}/>
                <div className="container" ref={listRef}>
                    <ListItem />
                    <ListItem />
                    <ListItem />
                    <ListItem />
                    <ListItem />
                    <ListItem />
                    <ListItem />
                    <ListItem />
                    <ListItem />

                </div>
                <ArrowForwardIosOutlined className="sliderArrow right" onClick={() =>handleClick('right')}/>
            </div>
        </div> 
    )
}

export default List
