    const [ position, setPosition ] = useState(0);
    const listRef = useRef();

    const handleClick = (direction) =>{

        if( direction === 'left'){
            setPosition(position-230)
            listRef.current.style.transform = `translateX(${position})`
            console.log(position)
        }else {
            setPosition(position+230)
            listRef.current.style.transform = `translateX(${position})`

